mapper.select:
select * from news

    valid = 1

        and news_name like CONCAT('%',#{newsName}, '%')


        and news_creator = #{newsCreator}


        and status = #{status}


order by status desc,update_time desc

mapper.update:
update news


        news_name=#{newsName},


        news_description=#{newsDescription},


        news_content=#{newsContent},


        status=#{status}


where news_id=#{newsId} and valid=1;
